import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header/Header';
import './AdminDashboard.css';
import { pendingSellers } from '../data/pendingSellers';
import { pendingProducts } from '../data/pendingProducts';
import { customerAccounts } from '../data/customerAccounts';
import { sellerAccounts } from '../data/sellerAccounts';
import { fraudReports } from '../data/fraudReports';
import { platformAnalytics } from '../data/platformAnalytics';
import { promotionalBanners } from '../data/promotionalBanners';

function AdminDashboard({ isLoggedIn, userName, userRole, onLogout }) {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sellers, setSellers] = useState(pendingSellers);
  const [products, setProducts] = useState(pendingProducts);
  const [customers, setCustomers] = useState(customerAccounts);
  const [sellersList, setSellersList] = useState(sellerAccounts);
  const [fraudList, setFraudList] = useState(fraudReports);
  const [banners, setBanners] = useState(promotionalBanners);
  const [accountFilter, setAccountFilter] = useState('all');
  const [fraudFilter, setFraudFilter] = useState('all');
  const [showBannerForm, setShowBannerForm] = useState(false);
  const [editingBanner, setEditingBanner] = useState(null);

  const handleApproveSeller = (sellerId) => {
    const seller = sellers.find(s => s.id === sellerId);
    if (seller) {
      alert(`‚úÖ Seller "${seller.businessName}" has been approved!\n\nApproval email sent to: ${seller.email}`);
      setSellers(sellers.filter(s => s.id !== sellerId));
    }
  };

  const handleRejectSeller = (sellerId) => {
    const seller = sellers.find(s => s.id === sellerId);
    if (seller) {
      const reason = prompt(`Enter rejection reason for "${seller.businessName}":`);
      if (reason) {
        alert(`‚ùå Seller "${seller.businessName}" has been rejected.\n\nReason: ${reason}\nNotification email sent to: ${seller.email}`);
        setSellers(sellers.filter(s => s.id !== sellerId));
      }
    }
  };

  const handleApproveProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (product) {
      alert(`‚úÖ Product "${product.name}" has been approved!\n\nIt will now be visible to customers.`);
      setProducts(products.filter(p => p.id !== productId));
    }
  };

  const handleRejectProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (product) {
      const reason = prompt(`Enter rejection reason for "${product.name}":`);
      if (reason) {
        alert(`‚ùå Product "${product.name}" has been rejected.\n\nReason: ${reason}\nSeller "${product.seller}" has been notified.`);
        setProducts(products.filter(p => p.id !== productId));
      }
    }
  };

  // Account Management Actions
  const handleSuspendAccount = (accountId, accountType) => {
    const list = accountType === 'customer' ? customers : sellersList;
    const setList = accountType === 'customer' ? setCustomers : setSellersList;
    const account = list.find(a => a.id === accountId);
    if (account) {
      const reason = prompt(`Enter reason for suspending ${account.name || account.businessName}:`);
      if (reason) {
        alert(`‚ö†Ô∏è Account "${account.name || account.businessName}" has been suspended.\n\nReason: ${reason}`);
        setList(list.map(a => a.id === accountId ? {...a, status: 'suspended', suspendedReason: reason} : a));
      }
    }
  };

  const handleActivateAccount = (accountId, accountType) => {
    const list = accountType === 'customer' ? customers : sellersList;
    const setList = accountType === 'customer' ? setCustomers : setSellersList;
    const account = list.find(a => a.id === accountId);
    if (account) {
      if (window.confirm(`Activate account for "${account.name || account.businessName}"?`)) {
        alert(`‚úÖ Account "${account.name || account.businessName}" has been reactivated.`);
        setList(list.map(a => a.id === accountId ? {...a, status: 'active', suspendedReason: undefined} : a));
      }
    }
  };

  const handleDeleteAccount = (accountId, accountType) => {
    const list = accountType === 'customer' ? customers : sellersList;
    const setList = accountType === 'customer' ? setCustomers : setSellersList;
    const account = list.find(a => a.id === accountId);
    if (account) {
      if (window.confirm(`‚ö†Ô∏è Are you sure you want to permanently delete "${account.name || account.businessName}"?\n\nThis action cannot be undone.`)) {
        alert(`üóëÔ∏è Account "${account.name || account.businessName}" has been deleted.`);
        setList(list.filter(a => a.id !== accountId));
      }
    }
  };

  // Fraud Monitor Actions
  const handleInvestigateFraud = (fraudId) => {
    setFraudList(fraudList.map(f => f.id === fraudId ? {...f, status: 'investigating'} : f));
    alert('Investigation started. Status updated to "Investigating".');
  };

  const handleResolveFraud = (fraudId) => {
    const fraud = fraudList.find(f => f.id === fraudId);
    if (fraud) {
      const action = prompt(`Enter resolution action for this ${fraud.type} fraud case:`);
      if (action) {
        setFraudList(fraudList.map(f => f.id === fraudId ? {...f, status: 'resolved', resolvedAction: action} : f));
        alert(`‚úÖ Fraud case resolved.\n\nAction taken: ${action}`);
      }
    }
  };

  const handleDismissFraud = (fraudId) => {
    if (window.confirm('Dismiss this fraud report as false alarm?')) {
      setFraudList(fraudList.filter(f => f.id !== fraudId));
      alert('Fraud report dismissed.');
    }
  };

  // Banner Management Actions
  const handleCreateBanner = (bannerData) => {
    const newBanner = {
      id: `banner${Date.now()}`,
      ...bannerData,
      impressions: 0,
      clicks: 0
    };
    setBanners([...banners, newBanner]);
    setShowBannerForm(false);
    alert('‚úÖ Banner created successfully!');
  };

  const handleEditBanner = (bannerId) => {
    const banner = banners.find(b => b.id === bannerId);
    setEditingBanner(banner);
    setShowBannerForm(true);
  };

  const handleUpdateBanner = (bannerData) => {
    setBanners(banners.map(b => b.id === editingBanner.id ? {...b, ...bannerData} : b));
    setShowBannerForm(false);
    setEditingBanner(null);
    alert('‚úÖ Banner updated successfully!');
  };

  const handleDeleteBanner = (bannerId) => {
    if (window.confirm('Delete this banner?')) {
      setBanners(banners.filter(b => b.id !== bannerId));
      alert('üóëÔ∏è Banner deleted.');
    }
  };

  const handleToggleBannerStatus = (bannerId) => {
    setBanners(banners.map(b => {
      if (b.id === bannerId) {
        const newStatus = b.status === 'active' ? 'inactive' : 'active';
        return {...b, status: newStatus};
      }
      return b;
    }));
  };

  return (
    <div className="admin-dashboard">
      <Header 
        cartCount={0} 
        onLoginClick={() => {}}
        isLoggedIn={isLoggedIn}
        userName={userName}
        userRole={userRole}
        onLogout={onLogout}
      />

      <div className="admin-header">
        <div className="admin-header-content">
          <h1>üõ°Ô∏è Admin Dashboard</h1>
          <button className="back-home-btn" onClick={() => navigate('/')}>
            ‚Üê Back to Home
          </button>
        </div>
      </div>

      <div className="admin-container">
        <div className="admin-tabs">
          <button
            className={`admin-tab ${activeTab === 'sellers' ? 'active' : ''}`}
            onClick={() => setActiveTab('sellers')}
          >
            <span className="tab-icon">üè™</span>
            <span className="tab-text">Pending Sellers</span>
            <span className="tab-badge">{sellers.length}</span>
          </button>
          <button
            className={`admin-tab ${activeTab === 'products' ? 'active' : ''}`}
            onClick={() => setActiveTab('products')}
          >
            <span className="tab-icon">üì¶</span>
            <span className="tab-text">Pending Products</span>
            <span className="tab-badge">{products.length}</span>
          </button>
        </div>

        <div className="admin-content">
          {activeTab === 'sellers' ? (
            <div className="sellers-section">
              <h2>Seller Registrations Awaiting Approval</h2>

              {sellers.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">‚úÖ</div>
                  <h3>All Caught Up!</h3>
                  <p>No pending seller registrations at the moment.</p>
                </div>
              ) : (
                <div className="sellers-grid">
                  {sellers.map(seller => (
                    <div key={seller.id} className="seller-card">
                      <div className="seller-card-header">
                        <div className="seller-info">
                          <h3>{seller.businessName}</h3>
                          <p className="seller-owner">{seller.ownerName}</p>
                        </div>
                        <div className="seller-date">
                          <span className="date-label">Submitted</span>
                          <span className="date-value">{seller.submittedDate}</span>
                        </div>
                      </div>

                      <div className="seller-details">
                        <div className="detail-row">
                          <span className="detail-label">üìß Email:</span>
                          <span className="detail-value">{seller.email}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">üìû Phone:</span>
                          <span className="detail-value">{seller.phone}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">üìç Address:</span>
                          <span className="detail-value">{seller.businessAddress}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">üßæ GST:</span>
                          <span className="detail-value">{seller.gstNumber}</span>
                        </div>
                        <div className="detail-row">
                          <span className="detail-label">üè¶ Bank:</span>
                          <span className="detail-value">{seller.bankAccount}</span>
                        </div>
                      </div>

                      <div className="seller-actions">
                        <button
                          className="approve-btn"
                          onClick={() => handleApproveSeller(seller.id)}
                        >
                          ‚úÖ Approve
                        </button>
                        <button
                          className="reject-btn"
                          onClick={() => handleRejectSeller(seller.id)}
                        >
                          ‚ùå Reject
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ) : (
            <div className="products-section">
              <h2>Product Listings Awaiting Approval</h2>

              {products.length === 0 ? (
                <div className="empty-state">
                  <div className="empty-icon">‚úÖ</div>
                  <h3>All Caught Up!</h3>
                  <p>No pending product listings at the moment.</p>
                </div>
              ) : (
                <div className="products-grid">
                  {products.map(product => (
                    <div key={product.id} className="product-approval-card">
                      <div className="product-approval-header">
                        <div className="product-emoji">{product.emoji}</div>
                        <div className="product-date">
                          <span className="date-label">Submitted</span>
                          <span className="date-value">{product.submittedDate}</span>
                        </div>
                      </div>

                      <h3 className="product-name">{product.name}</h3>
                      <p className="product-category">
                        Category: <strong>{product.category}</strong>
                      </p>
                      <p className="product-description">{product.description}</p>

                      <div className="product-pricing">
                        <span className="product-price">‚Çπ{product.price}</span>
                        <span className="product-original">‚Çπ{product.original}</span>
                        <span className="product-discount">
                          {Math.round(((product.original - product.price) / product.original) * 100)}% OFF
                        </span>
                      </div>

                      <div className="product-seller-info">
                        <span className="seller-label">üë§ Seller:</span>
                        <span className="seller-name">{product.seller}</span>
                      </div>

                      <div className="product-actions">
                        <button
                          className="approve-btn"
                          onClick={() => handleApproveProduct(product.id)}
                        >
                          ‚úÖ Approve
                        </button>
                        <button
                          className="reject-btn"
                          onClick={() => handleRejectProduct(product.id)}
                        >
                          ‚ùå Reject
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
