import React, { useState } from 'react';
import './LoginModal.css';

const LoginModal = ({ isOpen, onClose, onLoginSuccess }) => {
  const [mode, setMode] = useState('login'); // 'login' or 'signup'
  const [loginType, setLoginType] = useState('customer');
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    phone: '',
    businessName: '',
    businessAddress: '',
    gstNumber: '',
    bankAccount: ''
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (mode === 'login') {
      alert(`Logged in successfully as ${loginType}!`);
      // Simulate successful login
      if (onLoginSuccess) {
        onLoginSuccess(formData.email.split('@')[0]); // Use email prefix as username
      }
      onClose();
    } else {
      if (loginType === 'seller') {
        alert(`Seller registration submitted for ${formData.businessName}!\n\nYour account is pending admin approval.`);
      } else {
        alert(`Account created successfully!\nYou can now login.`);
      }
      setMode('login');
      setFormData({ email: '', password: '', name: '', phone: '', businessName: '', businessAddress: '', gstNumber: '', bankAccount: '' });
    }
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2 className="modal-title">Login</h2>
          <button className="close-btn" onClick={onClose}>
            âœ•
          </button>
        </div>

        <div className="modal-tabs">
          <button
            className={`tab-btn ${loginType === 'customer' ? 'active' : ''}`}
            onClick={() => setLoginType('customer')}
          >
            Customer
          </button>
          <button
            className={`tab-btn ${loginType === 'seller' ? 'active' : ''}`}
            onClick={() => setLoginType('seller')}
          >
            Seller
          </button>
          <button
            className={`tab-btn ${loginType === 'admin' ? 'active' : ''}`}
            onClick={() => setLoginType('admin')}
          >
            Admin
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input type="email" required placeholder="Enter your email" />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input type="password" required placeholder="Enter your password" />
          </div>

          <button type="submit" className="submit-btn">
            Login as {loginType.charAt(0).toUpperCase() + loginType.slice(1)}
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginModal;
