import React, { useState } from 'react';
import './LoginModal.css';

const LoginModal = ({ isOpen, onClose }) => {
  const [loginType, setLoginType] = useState('customer');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Logging in as ${loginType}`);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2 className="modal-title">Login</h2>
          <button className="close-btn" onClick={onClose}>
            âœ•
          </button>
        </div>

        <div className="modal-tabs">
          <button
            className={`tab-btn ${loginType === 'customer' ? 'active' : ''}`}
            onClick={() => setLoginType('customer')}
          >
            Customer
          </button>
          <button
            className={`tab-btn ${loginType === 'seller' ? 'active' : ''}`}
            onClick={() => setLoginType('seller')}
          >
            Seller
          </button>
          <button
            className={`tab-btn ${loginType === 'admin' ? 'active' : ''}`}
            onClick={() => setLoginType('admin')}
          >
            Admin
          </button>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email</label>
            <input type="email" required placeholder="Enter your email" />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input type="password" required placeholder="Enter your password" />
          </div>

          <button type="submit" className="submit-btn">
            Login as {loginType.charAt(0).toUpperCase() + loginType.slice(1)}
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginModal;
