import React, { useState } from 'react';
import '../../styles/SellerDashboard/OrdersTab.css';

/**
 * OrdersTab Component
 * Manages seller's orders with filtering and status management
 * Props:
 * - orders: array of orders
 * - onOrderAction: function to handle order actions (ship, return approval)
 */
function OrdersTab({ orders, onOrderAction }) {
  const [filter, setFilter] = useState('all');

  // Filter orders based on selected filter
  const filteredOrders = filter === 'all' 
    ? orders 
    : orders.filter(order => order.status === filter);

  // Count orders by status
  const orderCounts = {
    all: orders.length,
    processing: orders.filter(o => o.status === 'processing').length,
    shipped: orders.filter(o => o.status === 'shipped').length,
    delivered: orders.filter(o => o.status === 'delivered').length,
    returnRequested: orders.filter(o => o.status === 'return-requested').length
  };

  return (
    <div className="orders-tab">
      <h2>Orders Management</h2>

      {/* Filter Buttons */}
      <div className="order-filters">
        <button 
          className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
          onClick={() => setFilter('all')}
        >
          All Orders ({orderCounts.all})
        </button>
        <button 
          className={`filter-btn ${filter === 'processing' ? 'active' : ''}`}
          onClick={() => setFilter('processing')}
        >
          ‚è≥ Processing ({orderCounts.processing})
        </button>
        <button 
          className={`filter-btn ${filter === 'shipped' ? 'active' : ''}`}
          onClick={() => setFilter('shipped')}
        >
          üöö Shipped ({orderCounts.shipped})
        </button>
        <button 
          className={`filter-btn ${filter === 'delivered' ? 'active' : ''}`}
          onClick={() => setFilter('delivered')}
        >
          ‚úÖ Delivered ({orderCounts.delivered})
        </button>
        <button 
          className={`filter-btn ${filter === 'return-requested' ? 'active' : ''}`}
          onClick={() => setFilter('return-requested')}
        >
          ‚Ü©Ô∏è Returns ({orderCounts.returnRequested})
        </button>
      </div>

      {/* Orders Table */}
      <div className="orders-table-container">
        {filteredOrders.length === 0 ? (
          <div className="empty-state">
            <div className="empty-icon">üì¶</div>
            <h3>No Orders Found</h3>
            <p>No orders match the current filter.</p>
          </div>
        ) : (
          <table className="orders-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Product</th>
                <th>Customer</th>
                <th>Quantity</th>
                <th>Total</th>
                <th>Status</th>
                <th>Payment</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredOrders.map(order => (
                <tr key={order.id}>
                  <td><strong>{order.id}</strong></td>
                  <td>{order.productName}</td>
                  <td>{order.customer}</td>
                  <td>{order.quantity}</td>
                  <td>‚Çπ{order.total.toLocaleString()}</td>
                  <td>
                    <span className={`status-badge ${order.status}`}>
                      {order.status === 'delivered' && '‚úÖ Delivered'}
                      {order.status === 'shipped' && 'üöö Shipped'}
                      {order.status === 'processing' && '‚è≥ Processing'}
                      {order.status === 'return-requested' && '‚Ü©Ô∏è Return Requested'}
                    </span>
                  </td>
                  <td>
                    <span className={`payment-badge ${order.payment}`}>
                      {order.payment === 'paid' ? '‚úÖ Paid' : '‚è≥ Pending'}
                    </span>
                  </td>
                  <td>{order.date}</td>
                  <td>
                    <div className="order-actions">
                      {order.status === 'processing' && (
                        <button 
                          className="action-btn ship"
                          onClick={() => onOrderAction(order.id, 'ship')}
                        >
                          üöö Ship
                        </button>
                      )}
                      {order.status === 'return-requested' && (
                        <button 
                          className="action-btn approve-return"
                          onClick={() => onOrderAction(order.id, 'approve-return')}
                        >
                          ‚úÖ Approve
                        </button>
                      )}
                      {(order.status === 'delivered' || order.status === 'shipped') && (
                        <span className="no-action">‚Äî</span>
                      )}
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
}

export default OrdersTab;
