import React, { useState } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import './SellerDashboard.css';

const SellerDashboard = ({ isLoggedIn, userName, userRole, onLoginSuccess, onLogout }) => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [showAddModal, setShowAddModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);

  // Products State
  const [myProducts, setMyProducts] = useState([
    {
      id: 1,
      name: 'Wireless Headphones Pro',
      category: 'electronics',
      price: 3999,
      discount: 20,
      stock: 50,
      sold: 245,
      image: 'ðŸŽ§',
      description: 'Premium wireless headphones with noise cancellation',
      status: 'approved',
      revenue: 979755
    },
    {
      id: 2,
      name: 'Smart Watch Series 5',
      category: 'electronics',
      price: 8999,
      discount: 15,
      stock: 30,
      sold: 128,
      image: 'âŒš',
      description: 'Advanced fitness tracking smartwatch',
      status: 'approved',
      revenue: 1151872
    },
    {
      id: 3,
      name: 'Bluetooth Speaker',
      category: 'electronics',
      price: 2499,
      discount: 10,
      stock: 75,
      sold: 89,
      image: 'ðŸ”Š',
      description: 'Portable wireless speaker with amazing sound',
      status: 'pending',
      revenue: 222411
    }
  ]);

  // Orders State
  const [orders, setOrders] = useState([
    {
      id: '#ORD-1001',
      productName: 'Wireless Headphones Pro',
      customer: 'Rajesh Kumar',
      quantity: 2,
      total: 7998,
      status: 'delivered',
      date: '2024-01-15',
      payment: 'paid'
    },
    {
      id: '#ORD-1002',
      productName: 'Smart Watch Series 5',
      customer: 'Priya Sharma',
      quantity: 1,
      total: 8999,
      status: 'shipped',
      date: '2024-01-16',
      payment: 'paid'
    },
    {
      id: '#ORD-1003',
      productName: 'Bluetooth Speaker',
      customer: 'Amit Patel',
      quantity: 3,
      total: 7497,
      status: 'processing',
      date: '2024-01-17',
      payment: 'pending'
    },
    {
      id: '#ORD-1004',
      productName: 'Wireless Headphones Pro',
      customer: 'Sneha Reddy',
      quantity: 1,
      total: 3999,
      status: 'return-requested',
      date: '2024-01-14',
      payment: 'paid'
    }
  ]);

  const [formData, setFormData] = useState({
    name: '',
    category: 'electronics',
    price: '',
    discount: 0,
    stock: '',
    image: '',
    description: ''
  });

  const categories = [
    { value: 'electronics', label: 'ðŸ“± Electronics' },
    { value: 'clothing', label: 'ðŸ‘• Clothing' },
    { value: 'footwear', label: 'ðŸ‘Ÿ Footwear' },
    { value: 'accessories', label: 'ðŸ’ Accessories' },
    { value: 'home-decor', label: 'ðŸ  Home Decor' }
  ];

  // Analytics Data
  const analytics = {
    totalRevenue: myProducts.reduce((sum, p) => sum + (p.revenue || 0), 0),
    totalOrders: orders.length,
    totalSold: myProducts.reduce((sum, p) => sum + (p.sold || 0), 0),
    avgOrderValue: orders.reduce((sum, o) => sum + o.total, 0) / orders.length,
    revenueGrowth: 23.5,
    ordersThisMonth: 127,
    bestSelling: myProducts.sort((a, b) => (b.sold || 0) - (a.sold || 0))[0],
    lowStock: myProducts.filter(p => p.stock < 20)
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleAddProduct = (e) => {
    e.preventDefault();
    const newProduct = {
      id: Date.now(),
      ...formData,
      price: parseFloat(formData.price),
      discount: parseFloat(formData.discount),
      stock: parseInt(formData.stock),
      sold: 0,
      revenue: 0,
      status: 'pending'
    };
    setMyProducts([...myProducts, newProduct]);
    setShowAddModal(false);
    setFormData({ name: '', category: 'electronics', price: '', discount: 0, stock: '', image: '', description: '' });
    alert('Product submitted for admin approval!');
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
    setFormData({
      name: product.name,
      category: product.category,
      price: product.price,
      discount: product.discount,
      stock: product.stock,
      image: product.image,
      description: product.description
    });
    setShowAddModal(true);
  };

  const handleUpdateProduct = (e) => {
    e.preventDefault();
    const updatedProducts = myProducts.map(p => 
      p.id === editingProduct.id 
        ? { 
            ...p, 
            ...formData, 
            price: parseFloat(formData.price),
            discount: parseFloat(formData.discount),
            stock: parseInt(formData.stock),
            status: 'pending' 
          }
        : p
    );
    setMyProducts(updatedProducts);
    setShowAddModal(false);
    setEditingProduct(null);
    setFormData({ name: '', category: 'electronics', price: '', discount: 0, stock: '', image: '', description: '' });
    alert('Product updated and submitted for admin approval!');
  };

  const handleDeleteProduct = (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      setMyProducts(myProducts.filter(p => p.id !== id));
      alert('Product deleted successfully!');
