import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from '../components/Header/Header';
import './AdminDashboard.css';
import { pendingSellers } from '../data/pendingSellers';
import { pendingProducts } from '../data/pendingProducts';

function AdminDashboard({ isLoggedIn, userName, userRole, onLogout }) {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('sellers');
  const [sellers, setSellers] = useState(pendingSellers);
  const [products, setProducts] = useState(pendingProducts);

  const handleApproveSeller = (sellerId) => {
    const seller = sellers.find(s => s.id === sellerId);
    if (seller) {
      alert(`‚úÖ Seller "${seller.businessName}" has been approved!\n\nApproval email sent to: ${seller.email}`);
      setSellers(sellers.filter(s => s.id !== sellerId));
    }
  };

  const handleRejectSeller = (sellerId) => {
    const seller = sellers.find(s => s.id === sellerId);
    if (seller) {
      const reason = prompt(`Enter rejection reason for "${seller.businessName}":`);
      if (reason) {
        alert(`‚ùå Seller "${seller.businessName}" has been rejected.\n\nReason: ${reason}\nNotification email sent to: ${seller.email}`);
        setSellers(sellers.filter(s => s.id !== sellerId));
      }
    }
  };

  const handleApproveProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (product) {
      alert(`‚úÖ Product "${product.name}" has been approved!\n\nIt will now be visible to customers.`);
      setProducts(products.filter(p => p.id !== productId));
    }
  };

  const handleRejectProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    if (product) {
      const reason = prompt(`Enter rejection reason for "${product.name}":`);
      if (reason) {
        alert(`‚ùå Product "${product.name}" has been rejected.\n\nReason: ${reason}\nSeller "${product.seller}" has been notified.`);
        setProducts(products.filter(p => p.id !== productId));
      }
    }
  };

  return (
    <div className="admin-dashboard">
      <Header 
        cartCount={0} 
        onLoginClick={() => {}}
        isLoggedIn={isLoggedIn}
        userName={userName}
        userRole={userRole}
        onLogout={onLogout}
      />

      <div className="admin-header">
        <div className="admin-header-content">
          <h1>üõ°Ô∏è Admin Dashboard</h1>
          <button className="back-home-btn" onClick={() => navigate('/')}>
            ‚Üê Back to Home
          </button>
        </div>
      </div>

      <div className="admin-container">
        <div className="admin-tabs">
          <button
            className={`admin-tab ${activeTab === 'sellers' ? 'active' : ''}`}
            onClick={() => setActiveTab('sellers')}
          >
            <span className="tab-icon">üè™</span>
            <span className="tab-text">Pending Sellers</span>
            <span className="tab-badge">{sellers.length}</span>
          </button>
          <button
            className={`admin-tab ${activeTab === 'products' ? 'active' : ''}`}
            onClick={() => setActiveTab('products')}
          >
            <span className="tab-icon">üì¶</span>
            <span className="tab-text">Pending Products</span>
            <span className="tab-badge">{products.length}</span>
          </button>
        </div>

        <div className="admin-content">
          {activeTab === 'sellers' ? (
            <div className="sellers-section">
