import React, { useState } from 'react';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import Carousel from '../components/Carousel/Carousel';
import ProductSection from '../components/ProductSection/ProductSection';
import LoginModal from '../components/LoginModal/LoginModal';

// Import data
import { slides } from '../data/slides';
import { electronics, clothes, shoes, accessories, homeDecor } from '../data/products';

const HomePage = () => {
  const [cartCount, setCartCount] = useState(0);
  const [showLoginModal, setShowLoginModal] = useState(false);

  // Carousel slides
  const slides = [
    {
      title: 'Summer Sale - Up to 70% Off',
      description: 'Shop the latest trends in fashion and electronics',
      bg: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
    },
    {
      title: 'New Arrivals - Just Dropped',
      description: 'Discover the newest products across all categories',
      bg: 'linear-gradient(135deg, #f093fb 0%, #f5576c 100%)',
    },
    {
      title: 'Free Shipping on Orders $50+',
      description: 'Get your favorite items delivered to your door',
      bg: 'linear-gradient(135deg, #4facfe 0%, #00f2fe 100%)',
    },
    {
      title: 'Smart Shopping with AI',
      description: 'Personalized recommendations just for you',
      bg: 'linear-gradient(135deg, #43e97b 0%, #38f9d7 100%)',
    },
  ];

  // Product data
  const electronics = [
    { id: 1, name: 'Wireless Headphones', price: 2999, original: 4999, rating: 4.5, emoji: 'üéß' },
    { id: 2, name: 'Smart Watch', price: 4999, original: 7999, rating: 4.8, emoji: '‚åö' },
    { id: 3, name: 'Laptop Pro', price: 54999, original: 69999, rating: 4.7, emoji: 'üíª' },
    { id: 4, name: 'Bluetooth Speaker', price: 1999, original: 2999, rating: 4.3, emoji: 'üîä' },
    { id: 5, name: 'Smartphone', price: 24999, original: 29999, rating: 4.6, emoji: 'üì±' },
  ];

  const clothes = [
    { id: 6, name: 'Denim Jacket', price: 1499, original: 2499, rating: 4.4, emoji: 'üß•' },
    { id: 7, name: 'Cotton T-Shirt', price: 499, original: 799, rating: 4.2, emoji: 'üëï' },
    { id: 8, name: 'Formal Shirt', price: 899, original: 1499, rating: 4.5, emoji: 'üëî' },
    { id: 9, name: 'Summer Dress', price: 1299, original: 1999, rating: 4.6, emoji: 'üëó' },
    { id: 10, name: 'Hoodie', price: 999, original: 1599, rating: 4.3, emoji: 'üß¢' },
  ];

  const shoes = [
    { id: 11, name: 'Running Shoes', price: 2499, original: 3999, rating: 4.7, emoji: 'üëü' },
    { id: 12, name: 'Casual Sneakers', price: 1999, original: 2999, rating: 4.4, emoji: 'üëû' },
    { id: 13, name: 'Formal Shoes', price: 2999, original: 4499, rating: 4.5, emoji: 'üëû' },
    { id: 14, name: 'Sports Shoes', price: 3499, original: 4999, rating: 4.8, emoji: '‚öΩ' },
    { id: 15, name: 'Sandals', price: 799, original: 1299, rating: 4.2, emoji: 'ü©¥' },
  ];

  const accessories = [
    { id: 16, name: 'Leather Wallet', price: 699, original: 1199, rating: 4.3, emoji: 'üëõ' },
    { id: 17, name: 'Sunglasses', price: 899, original: 1499, rating: 4.5, emoji: 'üï∂Ô∏è' },
    { id: 18, name: 'Backpack', price: 1299, original: 1999, rating: 4.6, emoji: 'üéí' },
    { id: 19, name: 'Watch Band', price: 499, original: 799, rating: 4.2, emoji: '‚åö' },
    { id: 20, name: 'Phone Case', price: 299, original: 499, rating: 4.4, emoji: 'üì±' },
  ];

  const homeDecor = [
    { id: 21, name: 'Table Lamp', price: 1499, original: 2499, rating: 4.5, emoji: 'üí°' },
    { id: 22, name: 'Wall Clock', price: 899, original: 1499, rating: 4.3, emoji: 'üïê' },
    { id: 23, name: 'Cushion Set', price: 699, original: 1199, rating: 4.4, emoji: 'üõãÔ∏è' },
    { id: 24, name: 'Photo Frame', price: 399, original: 699, rating: 4.2, emoji: 'üñºÔ∏è' },
    { id: 25, name: 'Plant Pot', price: 299, original: 499, rating: 4.6, emoji: 'ü™¥' },
  ];

  // Auto-advance carousel
  useEffect(() => {
    const timer = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);
    return () => clearInterval(timer);
  }, [slides.length]);

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const addToCart = (product) => {
    setCartCount(cartCount + 1);
    alert(`Added ${product.name} to cart!`);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    alert(`Logging in as ${loginType}`);
    setShowLoginModal(false);
  };

  const ProductCard = ({ product, category }) => (
    <div className="product-card">
      <div className="product-image">
        <span style={{ fontSize: '4rem' }}>{product.emoji}</span>
      </div>
      <div className="product-info">
        <div className="product-category">{category}</div>
        <h3 className="product-name">{product.name}</h3>
        <div className="product-rating">
          {'‚≠ê'.repeat(Math.floor(product.rating))} {product.rating}
        </div>
        <div className="product-price">
          ‚Çπ{product.price}
          <span className="original">‚Çπ{product.original}</span>
        </div>
        <button className="add-to-cart" onClick={() => addToCart(product)}>
          Add to Cart
        </button>
      </div>
    </div>
  );

  return (
    <div className="app">
      {/* Header */}
      <header className="header">
        <div className="container">
          <div className="header-content">
            <a href="/" className="logo">
              üõí S-E-Com
            </a>

            <div className="search-bar">
              <input
                type="text"
                className="search-input"
                placeholder="Search for products, brands and more..."
              />
              <button className="search-btn">Search</button>
            </div>

            <div className="nav-actions">
              <button className="cart-btn">
                üõí
                {cartCount > 0 && <span className="cart-count">{cartCount}</span>}
              </button>
              <button className="nav-btn" onClick={() => setShowLoginModal(true)}>
                Login
              </button>
              <button className="nav-btn primary">Sign Up</button>
            </div>
          </div>
        </div>
      </header>

      <div className="container">
        {/* Carousel */}
        <div className="carousel">
          <div className="carousel-track" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
            {slides.map((slide, index) => (
              <div key={index} className="carousel-slide" style={{ background: slide.bg }}>
                <div className="slide-content">
                  <h2>{slide.title}</h2>
                  <p>{slide.description}</p>
                  <button className="slide-btn">Shop Now</button>
                </div>
              </div>
            ))}
          </div>

          <button className="carousel-arrow left" onClick={prevSlide}>
            ‚Äπ
          </button>
          <button className="carousel-arrow right" onClick={nextSlide}>
            ‚Ä∫
          </button>

          <div className="carousel-nav">
            {slides.map((_, index) => (
              <div
                key={index}
                className={`carousel-dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => setCurrentSlide(index)}
              />
            ))}
          </div>
        </div>

        {/* Electronics Section */}
        <section className="section">
          <div className="section-header">
            <h2 className="section-title">üì± Electronics</h2>
            <a href="#" className="view-all">View All ‚Üí</a>
          </div>
          <div className="product-grid">
            {electronics.map((product) => (
              <ProductCard key={product.id} product={product} category="Electronics" />
            ))}
          </div>
        </section>

        {/* Clothes Section */}
        <section className="section">
          <div className="section-header">
            <h2 className="section-title">üëï Fashion & Clothing</h2>
            <a href="#" className="view-all">View All ‚Üí</a>
          </div>
          <div className="product-grid">
            {clothes.map((product) => (
              <ProductCard key={product.id} product={product} category="Clothing" />
            ))}
          </div>
        </section>

        {/* Shoes Section */}
        <section className="section">
          <div className="section-header">
            <h2 className="section-title">üëü Footwear</h2>
            <a href="#" className="view-all">View All ‚Üí</a>
          </div>
          <div className="product-grid">
            {shoes.map((product) => (
              <ProductCard key={product.id} product={product} category="Footwear" />
            ))}
          </div>
        </section>

        {/* Accessories Section */}
        <section className="section">
          <div className="section-header">
            <h2 className="section-title">üëú Accessories</h2>
            <a href="#" className="view-all">View All ‚Üí</a>
          </div>
          <div className="product-grid">
            {accessories.map((product) => (
              <ProductCard key={product.id} product={product} category="Accessories" />
            ))}
          </div>
        </section>

        {/* Home Decor Section */}
        <section className="section">
          <div className="section-header">
            <h2 className="section-title">üè† Home & Decor</h2>
            <a href="#" className="view-all">View All ‚Üí</a>
          </div>
          <div className="product-grid">
            {homeDecor.map((product) => (
              <ProductCard key={product.id} product={product} category="Home Decor" />
            ))}
          </div>
        </section>
      </div>

      {/* Footer */}
      <footer className="footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-section">
              <h3>About S-E-Com</h3>
              <a href="#">About Us</a>
              <a href="#">Careers</a>
              <a href="#">Press</a>
            </div>
            <div className="footer-section">
              <h3>Customer Service</h3>
              <a href="#">Help Center</a>
              <a href="#">Track Order</a>
              <a href="#">Returns</a>
            </div>
            <div className="footer-section">
              <h3>Sell With Us</h3>
              <a href="#">Become a Seller</a>
              <a href="#">Seller Dashboard</a>
              <a href="#">Seller Policy</a>
            </div>
            <div className="footer-section">
              <h3>Connect</h3>
              <a href="#">Facebook</a>
              <a href="#">Twitter</a>
              <a href="#">Instagram</a>
            </div>
          </div>
          <div className="footer-bottom">
            <p>¬© 2024 S-E-Com. All rights reserved.</p>
          </div>
        </div>
      </footer>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="modal-overlay" onClick={() => setShowLoginModal(false)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2 className="modal-title">Login</h2>
              <button className="close-btn" onClick={() => setShowLoginModal(false)}>
                ‚úï
              </button>
            </div>

            <div className="modal-tabs">
              <button
                className={`tab-btn ${loginType === 'customer' ? 'active' : ''}`}
                onClick={() => setLoginType('customer')}
              >
                Customer
              </button>
              <button
                className={`tab-btn ${loginType === 'seller' ? 'active' : ''}`}
                onClick={() => setLoginType('seller')}
              >
                Seller
              </button>
              <button
                className={`tab-btn ${loginType === 'admin' ? 'active' : ''}`}
                onClick={() => setLoginType('admin')}
              >
                Admin
              </button>
            </div>

            <form onSubmit={handleLogin}>
              <div className="form-group">
                <label>Email</label>
                <input type="email" required placeholder="Enter your email" />
              </div>

              <div className="form-group">
                <label>Password</label>
                <input type="password" required placeholder="Enter your password" />
              </div>

              <button type="submit" className="submit-btn">
                Login as {loginType.charAt(0).toUpperCase() + loginType.slice(1)}
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
